import React from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';

const mobileScreen = window.matchMedia("(max-width: 1600px )");
$(document).ready(function () {
    $(".sidebar-nav-dropdown-toggle").click(function () {
        $(this).closest(".sidebar-nav-dropdown")
            .toggleClass("show")
            .find(".sidebar-nav-dropdown")
            .removeClass("show");
        $(this).parent()
            .siblings()
            .removeClass("show");
    });
    $(".menu-toggle").click(function () {
        if (mobileScreen.matches) {
            $(".sidebar-nav").toggleClass("mobile-show");
        } else {
            $(".sidebar").toggleClass("sidebar-compact");
        }
    });
});

const Header = () => {
    return(
      <div className="header" >
          <header className="bg-header p-3 bg-lighter text-white">
              <div className="container" style={{ marginLeft: "auto", marginRight:"auto", maxWidth:"100%"}}>
                <div className="d-flex flex-wrap align-items-center justify-content-center justify-content-lg-start">
                  <div className="sidebar-nav">
                    <div className='row col-12 d-flex flex-row justify-content-center'>
                      <header>
                        <a href="/#" className="col-1 menu-toggle">
                          <i className="fas fa-bars"></i>
                        </a>
                        <a href="/#" className="col-11 d-flex align-items-center mb-2 mb-lg-0 text-white text-decoration-none" style={{ fontFamily: "Orbitron, sans-serif" }}>
                          <img className="bi me-2" width="120" height="86" alt="logo" src="/images/DreamTek.png" />
                        </a>
                      </header>
                    </div>
                    <nav className="sidebar-nav-list">
                        <div className='sidebar-nav-dropdown'>
                          <a href="/#" className="sidebar-nav-item sidebar-nav-dropdown-toggle"><span role="img" aria-label="desktop"><i className='me-2'></i>üìÇ</span> Cat√©gories </a>
                            <div className='sidebar-nav-dropdown-menu'>
                              <a href="/#" className="sidebar-nav-dropdown-item"><i className='me-5'></i><span role="img" aria-label="infinite">‚ôæÔ∏è</span><i className='me-2'></i> Toutes</a>
                              <a href="/#" className="sidebar-nav-dropdown-item"><i className='me-5'></i><span role="img" aria-label="laptop">üíª</span><i className='me-2'></i> ORDINATEURS PORTABLES</a>
                              <a href="/#" className="sidebar-nav-dropdown-item"><i className='me-5'></i><span role="img" aria-label="desktop">üñ•Ô∏è</span><i className='me-2'></i> ORDINATEURS DE BUREAU, ECRANS PC</a>
                              <a href="/#" className="sidebar-nav-dropdown-item"><i className='me-5'></i><span role="img" aria-label="controller">üéÆ</span><i className='me-2'></i> UNIVERS PC GAMING</a>
                              <a href="/#" className="sidebar-nav-dropdown-item"><i className='me-5'></i><span role="img" aria-label="bookopened">üìñ</span><i className='me-2'></i> TABLETTES, LISEUSES EBOOK</a>
                              <a href="/#" className="sidebar-nav-dropdown-item"><i className='me-5'></i><span role="img" aria-label="mouse">üñ±Ô∏è</span><i className='me-2'></i> P√âRIPH√âRIQUES & ACCESSOIRES</a>
                          </div>
                          <a href="/#" className="sidebar-nav-item"><span role="img" aria-label="desktop"><i className='me-2'></i>‚öôÔ∏è</span> Param√®tres</a>
                          <a href="/#" className="sidebar-nav-item"><span role="img" aria-label="desktop"><i className='me-2'></i>üë§</span> Profil</a>
                          <a href="/#" className="sidebar-nav-item"><span role="img" aria-label="desktop"><i className='me-2'></i>üõí</span> Panier</a>
                          <hr />
                        </div>
                      <a href="/#" className="sidebar-nav-item"><span role="img" aria-label="desktop"><i className='me-2'></i>üñêÔ∏è</span> D√©connexion </a>
                    </nav>
                  </div>
                  <ul className="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0" style={{ marginLeft: "auto", marginRight:"0"}}>
                    <a href="/#!" className="menu-toggle">
